	void app_update_keybinds()
	{
		VarType obj;
		BoolType check, navclear;
		navclear = false;
		for (IntType i = IntType(0); i < e_keybind_amount; i++)
		{
			obj = global::keybinds.Value(i);
			check = keybind_check(obj, /*"keyboard_check"*/ STR(704));
			idBool(obj, released) = false;
			if (idVar(obj, active) > 0)
				idBool(obj, released) = keybind_check(obj, /*"keyboard_check_released"*/ STR(705));
			idBool(obj, pressed) = keybind_check(obj, /*"keyboard_check_pressed"*/ STR(706));
			idVar(obj, active) = check;
			if (idBool(obj, pressed) && idBool(obj, navigation) && string_contains(global::_app->window_busy, /*"camera"*/ STR(126)))
				navclear = true;
		}
		if (navclear)
		{
			for (IntType i = IntType(0); i < e_keybind_amount; i++)
			{
				obj = global::keybinds.Value(i);
				if (!idBool(obj, navigation))
				{
					idBool(obj, pressed) = false;
					idVar(obj, active) = false;
				}
			}
		}
	}
	
